mapper.select:
        SELECT


--         级联表的字段
        ,kecheng.jiaolian_id as kechengJiaolianId
        ,kecheng.kecheng_name as kechengName
        ,kecheng.kecheng_photo as kechengPhoto
        ,kecheng.kecheng_price as kechengPrice
        ,kecheng.kecheng_types as kechengTypes
        ,kecheng.kecheng_kucun_number as kechengKucunNumber
        ,kecheng.kecheng_new_money as kechengNewMoney
        ,kecheng.kecheng_delete as kechengDelete
        ,kecheng.kecheng_content as kechengContent
        ,yonghu.yonghu_name as yonghuName
        ,yonghu.yonghu_photo as yonghuPhoto
        ,yonghu.yonghu_phone as yonghuPhone
        ,yonghu.yonghu_id_number as yonghuIdNumber
        ,yonghu.yonghu_email as yonghuEmail
        ,yonghu.new_money as newMoney
        ,yonghu.yonghu_jifen as yonghuJifen
        ,yonghu.huanyuan_types as huanyuanTypes
        ,yonghu.huiyuandaoqi_time as huiyuandaoqiTime
        ,yonghu.yonghu_delete as yonghuDelete

        FROM kecheng_order  a
        left JOIN kecheng kecheng ON a.kecheng_id = kecheng.id
        left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id



                and a.id in

                #{item}



                and a.kecheng_order_uuid_number like CONCAT('%',#{params.kechengOrderUuidNumber},'%')


                and (
                    a.kecheng_id = #{params.kechengId}
                )


                and (
                    a.yonghu_id = #{params.yonghuId}
                )


                  and a.buy_number >= #{params.buyNumberStart}   


                  and a.buy_number <= #{params.buyNumberEnd}   


                and a.buy_number = #{params.buyNumber}


                and a.kecheng_order_types = #{params.kechengOrderTypes}


                  and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


                  and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 




                and a.kecheng_id IS NOT NULL


                and kecheng.jiaolian_id = #{params.jiaolianId}


                and kecheng.kecheng_name like CONCAT('%',#{params.kechengName},'%')


                  and kecheng.kecheng_price >= #{params.kechengPriceStart}   


                  and kecheng.kecheng_price <= #{params.kechengPriceEnd}   


                and kecheng.kecheng_price = #{params.kechengPrice}


                and kecheng.kecheng_types = #{params.kechengTypes}



                  and kecheng.kecheng_kucun_number >= #{params.kechengKucunNumberStart}   


                  and kecheng.kecheng_kucun_number <= #{params.kechengKucunNumberEnd}   


                and kecheng.kecheng_kucun_number = #{params.kechengKucunNumber}


                  and kecheng.kecheng_new_money >= #{params.kechengNewMoneyStart}   


                  and kecheng.kecheng_new_money <= #{params.kechengNewMoneyEnd}   


                  and kecheng.kecheng_delete >= #{params.kechengDeleteStart}   


                  and kecheng.kecheng_delete <= #{params.kechengDeleteEnd}   


                and kecheng.kecheng_delete = #{params.kechengDelete}


                and kecheng.kecheng_content like CONCAT('%',#{params.kechengContent},'%')



                and a.yonghu_id IS NOT NULL


                and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')


                and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')


                and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')


                and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')


                  and yonghu.new_money >= #{params.newMoneyStart}   


                  and yonghu.new_money <= #{params.newMoneyEnd}   


                  and yonghu.yonghu_jifen >= #{params.yonghuJifenStart}   


                  and yonghu.yonghu_jifen <= #{params.yonghuJifenEnd}   


                and yonghu.huanyuan_types = #{params.huanyuanTypes}



                  and UNIX_TIMESTAMP(yonghu.huiyuandaoqi_time) >= UNIX_TIMESTAMP(#{params.huiyuandaoqiTimeStart}) 


                  and UNIX_TIMESTAMP(yonghu.huiyuandaoqi_time) <= UNIX_TIMESTAMP(#{params.huiyuandaoqiTimeEnd}) 


                  and yonghu.yonghu_delete >= #{params.yonghuDeleteStart}   


                  and yonghu.yonghu_delete <= #{params.yonghuDeleteEnd}   


                and yonghu.yonghu_delete = #{params.yonghuDelete}



        order by a.${params.orderBy} desc
