mapper.select:
SELECT
-- 级联表的字段 ,dianzhang.dianzhang_name as dianzhangName ,dianzhang.dianzhang_photo as dianzhangPhoto ,dianzhang.dianzhang_phone as dianzhangPhone ,dianzhang.yonghu_email as yonghuEmail ,dianzhang.yonghu_delete as yonghuDelete ,jiaolian.jiaolian_name as jiaolianName ,jiaolian.jiaolian_photo as jiaolianPhoto ,jiaolian.jiaolian_phone as jiaolianPhone ,jiaolian.yonghu_email as yonghuEmail ,jiaolian.yonghu_delete as yonghuDelete ,yonghu.yonghu_name as yonghuName ,yonghu.yonghu_photo as yonghuPhoto ,yonghu.yonghu_phone as yonghuPhone ,yonghu.yonghu_id_number as yonghuIdNumber ,yonghu.yonghu_email as yonghuEmail ,yonghu.new_money as newMoney ,yonghu.yonghu_jifen as yonghuJifen ,yonghu.huanyuan_types as huanyuanTypes ,yonghu.huiyuandaoqi_time as huiyuandaoqiTime ,yonghu.yonghu_delete as yonghuDelete ,users.username as uusername ,users.password as upassword ,users.role as urole ,users.addtime as uaddtime
FROM forum a left JOIN dianzhang dianzhang ON a.dianzhang_id = dianzhang.id left JOIN jiaolian jiaolian ON a.jiaolian_id = jiaolian.id left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id left JOIN users users ON a.users_id = users.id
and a.id in
#{item}
and a.forum_name like CONCAT('%',#{params.forumName},'%')
and ( a.yonghu_id = #{params.yonghuId} )
and ( a.jiaolian_id = #{params.jiaolianId} )
and a.dianzhang_id like CONCAT('%',#{params.dianzhangId},'%')
and ( a.users_id = #{params.usersId} )
and a.forum_content like CONCAT('%',#{params.forumContent},'%')
and a.super_ids >= #{params.superIdsStart}
and a.super_ids <= #{params.superIdsEnd}
and a.super_ids = #{params.superIds}
and a.forum_state_types = #{params.forumStateTypes}
and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and UNIX_TIMESTAMP(a.update_time) >= UNIX_TIMESTAMP(#{params.updateTimeStart})
and UNIX_TIMESTAMP(a.update_time) <= UNIX_TIMESTAMP(#{params.updateTimeEnd})
and a.dianzhang_id IS NOT NULL
and dianzhang.dianzhang_name like CONCAT('%',#{params.dianzhangName},'%')
and dianzhang.dianzhang_phone like CONCAT('%',#{params.dianzhangPhone},'%')
and dianzhang.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')
and dianzhang.yonghu_delete >= #{params.yonghuDeleteStart}
and dianzhang.yonghu_delete <= #{params.yonghuDeleteEnd}
and dianzhang.yonghu_delete = #{params.yonghuDelete}
and a.jiaolian_id IS NOT NULL
and jiaolian.jiaolian_name like CONCAT('%',#{params.jiaolianName},'%')
and jiaolian.jiaolian_phone like CONCAT('%',#{params.jiaolianPhone},'%')
and jiaolian.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')
and jiaolian.yonghu_delete >= #{params.yonghuDeleteStart}
and jiaolian.yonghu_delete <= #{params.yonghuDeleteEnd}
and jiaolian.yonghu_delete = #{params.yonghuDelete}
and a.yonghu_id IS NOT NULL
and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')
and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')
and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')
and yonghu.new_money >= #{params.newMoneyStart}
and yonghu.new_money <= #{params.newMoneyEnd}
and yonghu.yonghu_jifen >= #{params.yonghuJifenStart}
and yonghu.yonghu_jifen <= #{params.yonghuJifenEnd}
and yonghu.huanyuan_types = #{params.huanyuanTypes}
and UNIX_TIMESTAMP(yonghu.huiyuandaoqi_time) >= UNIX_TIMESTAMP(#{params.huiyuandaoqiTimeStart})
and UNIX_TIMESTAMP(yonghu.huiyuandaoqi_time) <= UNIX_TIMESTAMP(#{params.huiyuandaoqiTimeEnd})
and yonghu.yonghu_delete >= #{params.yonghuDeleteStart}
and yonghu.yonghu_delete <= #{params.yonghuDeleteEnd}
and yonghu.yonghu_delete = #{params.yonghuDelete}
and a.users_id IS NOT NULL
and users.username like CONCAT('%',#{params.username},'%')
and users.password like CONCAT('%',#{params.password},'%')
and users.role like CONCAT('%',#{params.role},'%')
and UNIX_TIMESTAMP(users.addtime) >= UNIX_TIMESTAMP(#{params.addtimeStart})
and UNIX_TIMESTAMP(users.addtime) <= UNIX_TIMESTAMP(#{params.addtimeEnd})
order by a.${params.orderBy} desc
